INSERT INTO `t_code_template_file` VALUES ('91', '86', '	layui.use([\'form\',\'layer\',\'laydate\',\'table\',\'laytpl\'],function(){\n	    var form = layui.form,\n	        layer = parent.layer === undefined ? layui.layer : top.layer,\n	        $ = layui.jquery,\n	        laydate = layui.laydate,\n	        laytpl = layui.laytpl,\n	        table = layui.table;\n		//列表数据\n		var tableIns = 	table.render({\n		        id : \"listTable\",\n				elem : \'#list\',\n				height :\"full-125\",\n				url :ctxPath+ \'/${boot.uncap(g.entityName)}/${boot.uncap(g.entityName)}List\' ,//数据接口\n				page : true, //开启分页,\n			        cols : [[\n			            {type: \"checkbox\", fixed:\"left\", width:50},\n						<%for(field in t.fields){%>\n							{field: \'${field.name}\', title: \'${field.comment}\', align:\"center\"},\n						<%}%>\n			            {field: \'account\', title: \'账号\', align:\"center\"},\n						{field: \'createTime\', title: \'创建时间\', align:\'center\', align:\'center\', templet:function(d){\n			                return _.formatDate(d.createTime);\n			            }},\n			            {title: \'操作\', width:170, fixed:\"right\",align:\"center\",templet:function(d){\n			            	return \'<a class=\"layui-btn layui-btn-xs boot-layer \" boot-title=\"修改用户\" boot-url=\"\'+ctxPath+\'/${boot.uncap(g.entityName)}/to${g.entityName}?id=\'+d.id+\'\">编辑</a>\'+\n			            	\'<a class=\"layui-btn layui-btn-xs layui-btn-danger boot-ajax \" boot-title=\"确认要删除吗\" boot-url=\"\'+ctxPath+\'/${boot.uncap(g.entityName)}/${boot.uncap(g.entityName)}Delete?id=\'+d.id+\'\"  >删除</a> \';\n			            }}\n			        ]]\n			});\n//==========================添加start==================\n\n\n		});', 'JavaScript', '2018-12-07 13:42:38', null);
