INSERT INTO `t_code_template_file` VALUES ('93', '88', 'package ${g.controllerPackage}.${g.moduleName};\n\nimport java.io.IOException;\nimport java.util.Date;\n\nimport javax.servlet.ServletException;\n\nimport com.ld.action.CreditManageBaseAction;\nimport com.ld.dao.model.PageNavigationBean;\nimport com.ld.dao.model.QueryModel;\nimport ${g.modelPackage}.${g.moduleName}.${g.entityName}Model;\nimport com.ld.mvc.annotation.Annotation.ActionName;\nimport com.ld.mvc.annotation.Annotation.MethodName;\nimport com.ld.util.CreditManageWebUtil;\n\n\n\n\n/**\n * ${g.name}控制器\n *\n * @author ${g.author}\n * @Date ${g.date}\n */\n @ActionName(\"/${g.moduleName}\")\n public class ${g.entityName}Action extends CreditManageBaseAction {\n   /**\n         * \n         * 进入列表页面\n         * @Title  search${g.entityName}\n         * @author ${g.author}\n         * @date ${g.date}\n         * @param models void\n         */\n   @MethodName(\"search${g.entityName}\")\n    public void search${g.entityName}(String... args) throws ServletException, IOException {\n        setAttr();\n        CreditManageWebUtil.forward(request, response, \"publish/${g.moduleName}/${g.moduleName}Search.jsp\");\n    }\n\n   /**\n         * \n         * 列表查询方法\n         * @Title  search${g.entityName}Result() \n         * @author ${g.author}\n         * @date ${g.date}\n         * @param models void\n         */\n    @MethodName(\"search${g.entityName}Result\")\n    public void search${g.entityName}Result() throws ServletException, IOException\n    {\n            setAttr();\n            QueryModel queryModel = new QueryModel();\n       <%for(field in t.fields){\n	if(field.isQuery!field.isQuery==2)\n          {\n             if(field.queryMode==\"1\")\n                {%>\n                   queryModel.addEq(\"${field.name}\",getString(\"${field.name}\"));\n                  <% }\n                  if(field.queryMode==\"7\")\n                {%>\n                   queryModel.addLike(\"${field.name}\",getString(\"${field.name}\"));\n                  <% }\n                }\n	 }%>\n            PageNavigationBean<${g.entityName}Model> pageBean = MYSQL_BASE_DAO.getPageNavigationBean(${g.entityName}Model.class, queryModel, pageNum,\n                    PAGE_SIZE);\n            request.setAttribute(\"pageBean\", pageBean);\n            CreditManageWebUtil.forward(request, response, \"publish/${g.moduleName}/${g.moduleName}List.jsp\");\n    }\n   /**\n         * \n         * 进入添加页面\n         * @Title  toAdd\n         * @author ${g.author}\n         * @date ${g.date}\n         * @param models void\n         */\n     @MethodName(\"toAdd\")\n    public void to${g.entityName}Add(String... args) throws ServletException, IOException {\n\n        CreditManageWebUtil.forward(request, response, \"publish/${g.moduleName}/${g.moduleName}Add.jsp\");\n    }\n   /**\n         * \n         * 进入修改页面\n         * @Title  toModify\n         * @author ${g.author}\n         * @date ${g.date}\n         * @param models void\n         */\n    @MethodName(\"toModify\")\n    public void to${g.entityName}Modify(String... args) throws ServletException, IOException {\n        QueryModel queryModel = new QueryModel();\n        Long id = getLong(\"id\");\n        queryModel.addEq(\"id\", id);\n        ${g.entityName}Model model = MYSQL_BASE_DAO.getOne(${g.entityName}Model.class, queryModel);\n        request.setAttribute(\"model\", model);\n        CreditManageWebUtil.forward(request, response, \"publish/${g.moduleName}/${g.moduleName}Modify.jsp\");\n    }\n\n       /**\n         * \n         * 批量删除\n         * @Title  batchDelete\n         * @author ${g.author}\n         * @date ${g.date}\n         * @param models void\n         */\n    @MethodName(\"batchDelete\")\n    public void batchDelete${g.entityName}(String... args) throws ServletException, IOException {\n        String[] idsArray = request.getParameterValues(\"id\");\n        boolean flag = MYSQL_BASE_DAO.batchDelete(${g.entityName}.class, idsArray);\n        if (flag) {\n            CreditManageWebUtil.sendOk(request, response, \"删除成功！\");\n        } else {\n            CreditManageWebUtil.sendError(request, response, \"删除失败！\");\n        }\n    }\n    \n      /**\n         * \n         * 查看详情\n         * @Title  toDetail\n         * @author ${g.author}\n         * @date ${g.date}\n         * @param models void\n         */\n    @MethodName(\"toDetail\")\n    public void toDetail${g.entityName}(String... args) throws ServletException, IOException {\n        QueryModel queryModel = new QueryModel();\n        Long id = getLong(\"id\");\n        queryModel.addEq(\"id\", id);\n        ${g.entityName}Model  model = MYSQL_BASE_DAO.getOne(${g.entityName}Model.class, queryModel);\n        request.setAttribute(\"model\", model);\n        CreditManageWebUtil.forward(request, response, \"publish/${g.moduleName}/${g.moduleName}Detail.jsp\");\n    }\n\n       /**\n         * \n         * 新增和修改\n         * @Title  addorupdate\n         * @author ${g.author}\n         * @date ${g.date}\n         * @param models void\n         */\n    @MethodName(\"addorupdate\")\n    public void addOrUpdate(String... args) throws ServletException, IOException {\n        Long id = getLong(\"id\");\n        if (id == null || id == 0L) {\n           ${g.entityName}Model  model = WebUtil.getModel(request, ${g.entityName}Model.class,true);\n              boolean  flag = MYSQL_BASE_DAO.save(model); // 新增、保存\n            if (flag) {                \n                CreditManageWebUtil.sendOk(request, response, \"新增信息成功！\");\n            } else {\n                CreditManageWebUtil.sendError(request, response, \"新增信息失败！\");\n            }\n        } else {\n              ${g.entityName}Model  model = WebUtil.getModel(request, ${g.entityName}Model.class,true);\n            boolean flag = MYSQL_BASE_DAO.update(model);\n            if (flag) {\n                CreditManageWebUtil.sendOk(request, response, \"更新成功！\");\n            } else {\n                CreditManageWebUtil.sendError(request, response, \"更新失败！\");\n            }\n        }\n    }\n\n     /**\n         * \n         * 删除\n         * @Title  delete\n         * @author ${g.author}\n         * @date ${g.date}\n         * @param models void\n         */\n    @MethodName(\"delete\")\n    public void delete(String... args) throws ServletException, IOException {\n        Long id = getLong(\"id\");\n        //boolean savaed = MYSQL_BASE_DAO.delete(${g.entityName}Model.class, id);\n        ${g.entityName}Model model = MYSQL_BASE_DAO.findById(${g.entityName}Model.class, id);\n        model.setState(Static.STATE_DEL);\n        boolean deleted =  MYSQL_BASE_DAO.update(model);\n        if (deleted) {\n            CreditManageWebUtil.sendOk(request, response, \"删除成功！\");\n            return;\n        }\n        CreditManageWebUtil.sendError(request, response, \"删除失败！\");\n    }\n\n}\n', 'Java', '2018-12-08 14:21:15', null);
INSERT INTO `t_code_template_file` VALUES ('94', '89', 'package ${g.daoPackage}.${g.moduleName};\n\n\nimport com.dao.CreditBaseDao;\nimport  ${g.modelPackage}.${g.moduleName}.${g.entityName}Model;\n/**\n * \n *  ${g.entityName} DAO操作类\n * @ClassName ${g.entityName}Dao \n * @author ${g.author}\n * @Date ${g.date}\n *\n */\npublic class ${g.entityName}Dao extends CreditBaseDao<${g.entityName}Model> {\n    public  ${g.entityName}Dao() {\n        super(\"${g.tableName}\");\n    }\n    \n    public int batchDelete(String[] idsArray) {\n        String sql = \"update \" + tableName + \" set state = ? where id = ?\";\n        Object[][] params = new Object[idsArray.length][2];\n        int i = 0;\n        for (String id : idsArray) {\n            params[i][0] = 1;\n            params[i][1] = id;\n            i++;\n        }\n        return batchUpdate(sql, params);\n    }\n    \n}\n', 'C', '2018-12-08 14:21:54', null);
INSERT INTO `t_code_template_file` VALUES ('95', '90', '<%@ page pageEncoding=\"UTF-8\"%>\n<%@ taglib uri=\"http://java.sun.com/jsp/jstl/core\" prefix=\"c\"%>\n<%@ taglib uri=\"http://java.sun.com/jsp/jstl/functions\" prefix=\"fn\"%>\n<div>\n	<form action=\"/${g.moduleName}/search${g.entityName}Result\" method=\"post\" t=\"searchResult\" class=\"searchForm\">\n		<div class=\"searchForm\">\n	 <#list t.fields as field>\n         <#if field.isQuery?? >\n            <#if field.isQuery==2 >\n              <b> ${field.chinaName}:</b><input  type=\"text\" name=\"${field.name}\" />\n           </#if>\n        </#if>\n        </#list>\n			<input class=\"bnt1\" type=\"submit\" id=\"doSearch\" name=\"doSearch\" value=\"查询\">\n	           \n		</div>\n		<div id=\"searchResult\" class=\"searchResult\"></div>\n	</form>\n</div>', 'Html', '2018-12-08 14:22:34', null);
INSERT INTO `t_code_template_file` VALUES ('96', '91', '	// 新闻发布\n	var init${g.entityName} = function(data) {// 初始化信息发布页面\n		\n		var initImageUpload = function() {// 初始化首页图片上传\n			var imageUpload = findAndInitEle(\'.imageUpload\');\n			if (imageUpload.length > 0) {\n				var actionUrl = uploadRootPath + \'/indexImgUpload\' + uploadCallbackURL;\n				imageUpload.each(function() {\n					var $this = $(this);\n					var formE = $this.closest(\'.formtr\');\n					if (formE.length == 0)\n						formE = $this.closest(\'form\');\n					initUpload($this, actionUrl, \'image\', function(json) {\n						formE.find(\'[name=imageNews]\').attr(\'src\', json.url);\n						formE.find(\'[name=image][type=\"hidden\"]\').val(json.url);\n					});\n				});\n			}\n		}\n		\n		var initCheckAllAndButton = function() {\n			// 全选\n			contentE.find(\'#checkall\').change(function() {\n				var checked = $(this).is(\':checked\');\n				contentE.find(\':checkbox\').each(function() {\n					this.checked = checked;\n				});\n			});\n			// 新增\n			contentE.find(\'.doAdd\').click(function() {\n				var typeId = $(\"#typeId\").val();\n				var newsType = $(\"#newsType\").val();\n				dialog(\'/${g.moduleName}/toAdd\', {\n					typeId : typeId,\n					newsType : newsType\n				}, function(ele, dialog) {\n					initSelect($(\"#typeId\"));\n					initImageUpload();\n					initEditor();\n					initSubmit(ele, function() {\n						dialog.remove();\n						contentE.find(\"#doSearch\").click();\n					});\n				}, \'新增信息发布\', 1000);\n			});\n			\n			\n			\n			// 删除\n			contentE.find(\'.doDelete\').click(function() {\n				var $this = $(this);\n				if (contentE.find(\":checkbox:checked[name=\'id\']\").length == 0) {\n					alert(\'至少选中一条信息！\');\n					$this.prop(\"disabled\", false);\n				} else if (confirm(\"确定要删除选中的信息吗?\")) {\n					var params = contentE.find(\":checkbox[name=\'id\']\").serialize() + \"&typeId=\" + $(\"#typeId\").val();\n					var url = \"/${g.moduleName}/batchDelete\";\n					ajax.getJSON(url, params, function(data) {\n						contentE.find(\"form\").submit();\n					});\n				} else {\n					$this.prop(\"disabled\", false);\n				}\n			});\n			\n			\n			// 详情\n			contentE.find(\'.detail\').click(function() {\n				var id = $(this).attr(\'v\');\n				dialog(\'/infNews/toDetail\', {\n					id : id\n				}, function() {\n				}, \'信息详情\', 1000);\n			});\n			\n			contentE.find(\'.modify\').click(function() {\n				var id = $(this).attr(\'v\');\n				var newsType = $(\"#newsType\").val();\n				dialog(\'/${g.moduleName}/toModify\', {\n					id : id,\n					newsType : newsType\n				}, function(ele, dialog) {\n					ele.find(\'#demo-htmlselect\').ddslick({ width: 125,height:200,onSelected: function(selectedData){  \n						var defaultPhoto=$(\".dd-selected-value\").val();\n						ele.find(\"#newsimgSource\").val(defaultPhoto);\n				    }});\n					initSelect($(\"#typeId\"));\n					initEditor();\n					initImageUpload();\n					initSubmit(ele, function() {\n						dialog.remove();\n						// contentE.find(\"#doSearch\").click();\n						contentE.find(\'form\').submit();\n					});\n				}, \'信息修改\', 1000);\n			});\n	\n		};\n		initSubmit(contentE, initCheckAllAndButton);\n		contentE.find(\'form\').submit();\n	};', 'JavaScript', '2018-12-08 14:23:08', null);
