INSERT INTO `t_code_template_file` VALUES ('101', '96', 'package ${g.modelPackage}.${g.moduleName};\nimport lombok.Data;\nimport com.ld.dao.annotation.Annotation.TableName;\nimport com.ld.mvc.annotation.Annotation.ColumnName;\n/**\n * ClassName: ${g.entityName}Model\n * \n * @author ${g.author}\n * @Date ${g.date}\n */\n@Data\n@TableName(\"${g.tableName}\")\npublic class ${g.entityName}Model {\n\n	<%for(field in t.fields){%>\n		     /**\n		     * ${field.chinaName}\n		     * ${field.comment}\n		     */\n           ${@org.apache.commons.lang.StringUtils.contains(field.fieldName, \"_\") ? \'@ColumnName(\"\'+field.fieldName+\'\")\' }\n			private ${field.type} ${field.name};\n		\n		<%}%>\n}', 'Java', '2018-12-08 14:25:31', null);
INSERT INTO `t_code_template_file` VALUES ('102', '97', '<%@ page pageEncoding=\"UTF-8\"%>\n<%@ taglib uri=\"http://java.sun.com/jsp/jstl/core\" prefix=\"c\"%>\n<%@ taglib uri=\"http://java.sun.com/jsp/jstl/fmt\" prefix=\"fmt\"%>\n<%@ taglib uri=\"http://java.sun.com/jsp/jstl/functions\" prefix=\"fn\"%>\n<%@ taglib uri=\"http://www.kevin.com/util\" prefix=\"util\"%>\n<a href=\"/${g.moduleName}/toAdd\" class=\"shieldDialog\" viewTitle=\"添加\" viewWidth=\"800\"><input type=\"button\" class=\"toAdd\" value=\"添加\"></a>\n<a href=\"/${g.moduleName}/batchDelete\" paramSelector=\":checkbox[name=\'id\']\" class=\"shieldAjax\" viewTitle=\"确定要删除选中的信息吗?\">\n	<input type=\"button\" class=\"doDelete\" value=\"删除\">\n</a>\n<span>${g.name}</span>\n<span>\n\n<table class=\"list\" id=\"table\">\n	<thead>\n		<tr>\n			<th class=\"w40\"><input type=\"checkbox\" id=\"checkall\"/></th>\n          <#list t.fields as field>\n             <th>  ${field.chinaName}</th>\n        </#list>\n			<th class=\"archives_operate\">操作</th>\n		</tr>\n	</thead>\n	<tbody>\n<c:choose>\n	<c:when test=\"${\'$\'}{not empty pageBean.resultList }\">\n	<c:forEach var=\"model\" items=\"${\'$\'}{pageBean.resultList }\">\n       <tr id=\"${\'$\'}{model.id }\">\n			<td><input type=\"checkbox\" name=\"id\" value=\"${\'$\'}{model.id }\" /></td>\n   <#list t.fields as field>\n                       <td>${\'$\'}{model.${field.name}}</td>\n                     </#list>\n                     <td>		\n		           <a href=\"/${g.moduleName}/toDetail?id=${\'$\'}{model.id}\" class=\"shieldDialog\" viewTitle=\"查看\" viewWidth=\"800\">\n				<img src=\"/images/archive_view.png\" class=\"viewKw noajax c_p\" title=\"查看\" />\n			  </a>\n			  <a href=\"/${g.moduleName}/toModify?id=${\'$\'}{model.id}\" class=\"shieldDialog\" viewTitle=\"修改\" viewWidth=\"800\">\n				<img src=\"/images/archive_modify.png\" class=\"updateKw noajax c_p\" title=\"修改\" />\n			  </a>\n	                  <a href=\"/${g.moduleName}/delete?id=${\'$\'}{model.id} \" class=\"shieldAjax\" viewtitle=\"确定要删除该条信息吗？\">\n				<img src=\"/images/archiveitem_delete.png\"  class=\"delTemplate noajax c_p\" title=\"删除\"/>\n			 </a>\n		    </td>\n		</tr>\n	</c:forEach>\n	</c:when>\n	<c:otherwise>\n		<tr><td class=\"taCenter\" colspan=\"8\">暂无信息</td></tr>\n	</c:otherwise>\n</c:choose>\n</tbody>\n</table>\n<%@include file=\"/WEB-INF/view/include/pagination.jsp\"%>\n\n', 'Html', '2018-12-08 14:26:02', null);
