INSERT INTO `t_code_template_file` VALUES ('105', '100', 'package ${g.codePackage}.controller;\n\nimport java.io.IOException;\nimport java.util.Date;\nimport javax.servlet.http.HttpServletRequest;\nimport javax.servlet.http.HttpServletResponse;\n\nimport org.springframework.stereotype.Controller;\nimport org.springframework.web.bind.annotation.RequestMapping;\n\nimport com.ld.action.index.BaseController;\nimport com.ld.admin.config.Global;\nimport com.ld.aop.annotation.OperationLogDetail;\nimport com.ld.aop.enums.OperationType;\nimport com.ld.aop.enums.OperationUnit;\nimport com.ld.dao.model.PageNavigationBean;\nimport com.ld.dao.model.QueryModel;\n\n/**\n * ${g.name}控制器\n *\n * @author ${g.author}\n * @Date ${g.date}\n */\n @Controller\n@RequestMapping(\"/${boot.uncap(g.entityName)}\")\npublic class ${g.entityName}Controller extends BaseController {\n\n   @Autowired\n    private ${g.entityName}Service ${boot.uncap(g.entityName)}Service;\n\n\n    @RequestMapping(\"get${g.entityName}List\")\n    public String get${g.entityName}List(${g.entityName}Model model, Integer page, Integer limit) {\n        setAttr();\n        if (page == null || page <= 0) {\n            page = 1;\n        }\n        if (limit == null || limit <= 0) {\n            limit = 10;\n        }\n        QueryModel queryModel = new QueryModel();\n\n        PageNavigationBean<${g.entityName}Model> pageBean =${boot.uncap(g.entityName)}Service.getPageNavigationBean(${g.entityName}Model.class, queryModel,\n                page, limit);\n        request.setAttribute(\"pageBean\", pageBean);\n\n        return \"${boot.uncap(g.entityName)}/${boot.uncap(g.entityName)}List\";\n    }\n   \n\n    @RequestMapping(\"to${g.entityName}Add\")\n    public String to${g.entityName}Add(HttpServletRequest request, HttpServletResponse response) {\n        return \"${boot.uncap(g.entityName)}/${boot.uncap(g.entityName)}Add\";\n    }\n\n    @RequestMapping(\"to${g.entityName}Update\")\n    public String to${g.entityName}Update(@RequestParam(required = true) Long id, HttpServletRequest request, HttpServletResponse response) {\n        ${g.entityName}Model model = ${boot.uncap(g.entityName)}Service.findById(${g.entityName}Model.class, id);\n        request.setAttribute(\"model\", model);\n        return \"${boot.uncap(g.entityName)}/${boot.uncap(g.entityName)}Update\";\n    }\n\n    @OperationLogDetail(detail = \"添加${g.name}规则\", level = 3, operationUnit = OperationUnit.PICKCONTENTRULE, operationType = OperationType.INSERT)\n    @RequestMapping(\"add${g.entityName}\")\n    @ResponseBody\n    public JsonResult add${g.entityName}(${g.entityName}Model model, HttpServletRequest request, HttpServletResponse response) {\n        try {\n            model.setId(CmUtil.getPkId());\n			model.setState(Global.STATE_NORMAL);\n            boolean saved =${boot.uncap(g.entityName)}Service.save(model);\n            if (saved) {\n                return new JsonResult(200, \"添加${g.name}成功\");\n            } else {\n                return new JsonResult(500, \"添加${g.name}失败\");\n            }\n        } catch (Exception e) {\n            return new JsonResult(500, \"添加${g.name}失败\");\n        }\n    }\n\n @OperationLogDetail(detail = \"修改${g.name}规则\", level = 3, operationUnit = OperationUnit.PICKCONTENTRULE, operationType = OperationType.UPDATE)\n    @RequestMapping(\"update${g.entityName}\")\n    @ResponseBody\n    public JsonResult update${g.entityName}(${g.entityName}Model model, HttpServletRequest request, HttpServletResponse response) {\n        try {\n            boolean saved = ${boot.uncap(g.entityName)}Service.update(model);\n            if (saved) {\n                return new JsonResult(200, \"修改${g.name}成功\");\n            } else {\n                return new JsonResult(500, \"修改${g.name}失败\");\n            }\n        } catch (Exception e) {\n            return new JsonResult(500, \"修改${g.name}失败\");\n        }\n    }\n\n    @OperationLogDetail(detail = \"删除${g.name}规则\", level = 3, operationUnit = OperationUnit.PICKCONTENTRULE, operationType = OperationType.DELETE)\n    @RequestMapping(\"del${g.entityName}\")\n    @ResponseBody\n    public JsonResult del${g.entityName}(@RequestParam(required = true) Long id) {\n        ${g.entityName}Model model = ${boot.uncap(g.entityName)}Service.findById(${g.entityName}Model.class, id);\n        model.setState(Global.STATE_DEL);\n        boolean del = ${boot.uncap(g.entityName)}Service.update(model);\n        if (del) {\n            return new JsonResult(200, \"删除${g.name}成功！\");\n        } else {\n            return new JsonResult(500, \"删除${g.name}失败！\");\n        }\n    }\n\n} ', 'Java', '2018-12-08 14:29:32', null);
