INSERT INTO `t_code_template_file` VALUES ('108', '103', '\\<%@include file=\"/WEB-INF/view/include/mytags.jsp\"%>\n\\<%@ page language=\"java\" contentType=\"text/html; charset=utf-8\"\n    pageEncoding=\"utf-8\"%>\n<!DOCTYPE html>\n<html>\n<head>\n	<meta charset=\"utf-8\">\n	<title>${g.name}修改</title>\n	<meta name=\"renderer\" content=\"webkit\">\n	<meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge,chrome=1\">\n	<meta name=\"viewport\" content=\"width=device-width, initial-scale=1, maximum-scale=1\">\n	<meta name=\"apple-mobile-web-app-status-bar-style\" content=\"black\">\n	<meta name=\"apple-mobile-web-app-capable\" content=\"yes\">\n	<meta name=\"format-detection\" content=\"telephone=no\">\n	<link rel=\"stylesheet\" href=\"\\${ctxPath}/layui/css/layui.css\" media=\"all\" />\n	<link rel=\"stylesheet\" href=\"\\${ctxPath}/css/public.css\" media=\"all\" />\n</head>\n<body class=\"childrenBody\">\n<form class=\"layui-form \" id=\"update${g.entityName}Form\" >\n	<%for(field in t.fields){%>\n	<div class=\"layui-form-item\">\n		<label class=\"layui-form-label\"><span class=\"ld-red\">*</span>${field.chinaName}:</label>\n		<div class=\"layui-input-block\">\n			<input type=\"text\" class=\"layui-input \" id=\"${field.fieldName}\" name=\"${field.fieldName}\"  value=\"\\${model.${field.fieldName}}\"  lay-verify=\"required\" placeholder=\"${field.chinaName}\" />\n		</div>\n	</div>\n		\n		<%}%>	\n	\n	<div class=\"layui-form-item\">\n		<button class=\"layui-btn layui-block\" lay-filter=\"update${g.entityName}\" lay-submit>提交</button>\n	</div>\n</form>\n<script type=\"text/javascript\" src=\"\\${ctxPath}/layui/layui.js\"></script>\n<script type=\"text/javascript\">\nlayui.use([\'form\'],function(){\n	  var form = layui.form;\n	  form.on(\"submit(update${g.entityName})\",function(data){\n			var data=$(\"#update${g.entityName}Form\").serialize();\n			var url =\"\\${ctxPath}/${boot.uncap(g.entityName)}/update${g.entityName}?t=\"+Math.random();\n			$.operate.post(url,data);\n		})\n});\n</script>\n</body>\n</html>', 'Html', '2018-12-08 14:31:03', null);
