INSERT INTO `t_code_template_file` VALUES ('110', '105', '\\<%@include file=\"/WEB-INF/view/include/mytags.jsp\"%>\n\\<%@ page language=\"java\" contentType=\"text/html; charset=utf-8\"\n    pageEncoding=\"utf-8\"%>\n    <!DOCTYPE html>\n<html>\n<head>\n	<meta charset=\"utf-8\">\n	<title>${g.name}列表</title>\n	<meta name=\"renderer\" content=\"webkit\">\n	<meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge,chrome=1\">\n	<meta name=\"viewport\" content=\"width=device-width, initial-scale=1, maximum-scale=1\">\n	<meta name=\"apple-mobile-web-app-status-bar-style\" content=\"black\">\n	<meta name=\"apple-mobile-web-app-capable\" content=\"yes\">\n	<meta name=\"format-detection\" content=\"telephone=no\">\n</head>\n<body class=\"childrenBody\">\n	<blockquote class=\"layui-elem-quote quoteBox\">\n		<form class=\"layui-form\">\n			<div class=\"layui-inline\">\n				<div class=\"layui-input-inline\">\n					<!--搜索条件区域  -->\n					<input id=\"title\" value=\"${title}\" class=\"layui-input\"/>\n				</div>\n				<a class=\"layui-btn search_btn\" data-type=\"reload\"   onclick=\"\" >搜索</a>\n			</div>\n			<div class=\"layui-inline\">\n	\n				<a class=\"layui-btn layui-btn-normal\" onclick=\"$.modal.open(\'添加\',\'\\${ctxPath}/${boot.uncap(g.entityName)}/to${g.entityName}Add\')\">添加</a>\n			\n			</div>\n		</form>\n	</blockquote>\n<table class=\"layui-table\" id=\"table1\" lay-filter=\"table1\">\n  <colgroup>\n    <col width=\"150\">\n    <col width=\"200\">\n    <col>\n  </colgroup>\n  <thead>\n    <tr>\n		<%for(field in t.fields){%>\n             <th>  ${field.chinaName}</th>\n		<%}%>\n       <th>操作</th>\n    </tr>\n  </thead>\n  <tbody id=\"tableBody\">\n  	<c:forEach var=\"model\" items=\"\\${pageBean.resultList }\">\n    <tr >\n		<td  title=\"\\${index.index+1 }\">\\${index.index+1 }</td>\n	        <%for(field in t.fields){%>\n                       <td>\\${model.${field.fieldName}}</td>\n            <%}%>\n		<td>\n		<a class=\"layui-btn layui-btn-xs\" data-id=\"\\${model.id}\" onclick=\"$.modal.open(\'修改\',\'\\${ctxPath}/${boot.uncap(g.entityName)}/to${g.entityName}Update?id=\\${model.id }\')\" >修改</a>\n		<a class=\"layui-btn layui-btn-xs layui-btn-danger\"  data-id=\"\\${model.id}\"  onclick=\"delete${g.entityName}(\'\\${model.id}\')\" >删除</a>\n		</td>\n	</tr>\n    </c:forEach>\n  </tbody>\n</table>\n\n\\<%@include file=\"/WEB-INF/view/include/layuipagination.jsp\" %>\n	<script type=\"text/javascript\">\n	function delete${g.entityName}(id)\n	{\n	   $.modal.confirm(\'确定删除${g.name}吗？\',function(index){\n			var url = \"\\${ctxPath}/${boot.uncap(g.entityName)}/del${g.entityName}\";\n			$.operate.post(url,{id:id});\n	   });\n	}\n\n\n	</script>\n</body>\n</html>\n', 'Html', '2018-12-08 14:32:01', null);
