 = 3, operationUnit = OperationUnit.PICKCONTENTRULE, operationType = OperationType.DELETE)\n    @RequestMapping(\"del${g.entityName}\")\n    @ResponseBody\n    public JsonResult del${g.entityName}(@RequestParam(required = true) Long id) {\n        ${g.entityName}Model model = ${g.entityName}Service.findById(${g.entityName}Model.class, id);\n        ${g.entityName}Model.setState(Global.STATE_DEL);\n        boolean del = ${g.entityName}Service.update(model);\n        if (del) {\n            return new JsonResult(200, \"删除${g.name}成功！\");\n        } else {\n            return new JsonResult(500, \"删除${g.name}失败！\");\n        }\n    }\n\n} ', 'Java', '2018-12-12 16:19:33', null);
