INSERT INTO `t_code_template_file` VALUES ('166', '161', '	layui.use([\'form\',\'layer\',\'laydate\',\'table\',\'laytpl\'],function(){\n	    var form = layui.form,\n	        layer = parent.layer === undefined ? layui.layer : top.layer,\n	        $ = layui.jquery,\n	        laydate = layui.laydate,\n	        laytpl = layui.laytpl,\n	        table = layui.table;\n		//列表数据\n		var tableIns = 	table.render({\n		        id : \"listTable\",\n				elem : \'#list\',\n				height :\"full-125\",\n				url :ctxPath+ \'/${boot.uncap(g.entityName)}/${boot.uncap(g.entityName)}List\' ,//数据接口\n				page : true, //开启分页,\n			        cols : [[\n			            {type: \"checkbox\", fixed:\"left\", width:50},\n						<%for(field in t.fields){%>\n							{field: \'${field.name}\', title: \'${field.chinaName}\', align:\"center\"},\n						<%}%>\n						{field: \'createTime\', title: \'创建时间\', align:\'center\', align:\'center\', templet:function(d){\n			                return _.formatDate(d.createTime);\n			            }},\n			            {title: \'操作\', width:170, fixed:\"right\",align:\"center\",templet:function(d){\n			            	return \'<a class=\"layui-btn layui-btn-xs boot-layer \" boot-title=\"修改用户\" boot-url=\"\'+ctxPath+\'/${boot.uncap(g.entityName)}/to${g.entityName}?id=\'+d.id+\'\">编辑</a>\'+\n			            	\'<a class=\"layui-btn layui-btn-xs layui-btn-danger boot-ajax \" boot-title=\"确认要删除吗\" boot-url=\"\'+ctxPath+\'/${boot.uncap(g.entityName)}/${boot.uncap(g.entityName)}Delete?id=\'+d.id+\'\"  >删除</a> \';\n			            }}\n			        ]]\n			});\n//==========================添加start==================\n\n\n		});', 'JavaScript', '2019-02-02 23:03:13', null);
INSERT INTO `t_code_template_file` VALUES ('167', '162', '<!-- 让IE8/9支持媒体查询，从而兼容栅格 -->\n<!--[if lt IE 9]>\n      <script src=\"https://cdn.staticfile.org/html5shiv/r29/html5.min.js\"></script>\n      <script src=\"https://cdn.staticfile.org/respond.js/1.4.2/respond.min.js\"></script>\n    <![endif]-->\n<!DOCTYPE html>\n<html>\n<head>\n<meta charset=\"utf-8\">\n<title>用户管理</title>\n @include(\"/common/cssAndJs.html\"){}\n</head>\n<body class=\"layui-anim layui-anim-up\">\n<div class=\"x-body\">\n		<div class=\"layui-row\">\n			<!-- 后期再写一个js的搜索框  或者改写tree数据 支持查询的序列id -->\n<!-- 			<form  id=\"searchForm\"  class=\"layui-form layui-col-md12 x-so searchForm\" onsubmit=\"return false\" >\n				<input type=\"text\" name=\"simpleName\" placeholder=\"请输入部门全称\"  autocomplete=\"off\" class=\"layui-input\">\n				<button id=\"search_btn\"  class=\"layui-btn\">\n					<i class=\"layui-icon\"></i> \n				</button>\n			</form> -->\n		</div>\n		<button class=\"layui-btn boot-layer  \" boot-title=\"添加${g.name}\" boot-url=\"\\${ctxPath}/${boot.uncap(g.entityName)}/to${g.entityName}Add\"  >\n			<i class=\"layui-icon\"></i>添加\n		</button>\n		<button id=\"btn-expand\" class=\"layui-btn\" >\n			<i class=\"iconfont\">&#xe6a7;</i>展开全部\n		</button>\n		<button id=\"btn-fold\" class=\"layui-btn\"   >\n			<i class=\"iconfont\">&#xe6a5;</i>折叠全部\n		</button>\n		<span class=\"x-right\" style=\"line-height: 40px\">\n			<button id=\"btn-refresh\" class=\"layui-btn\" >\n			<i class=\"iconfont\">&#xe6aa;</i>  刷新数据\n		</button>\n		</span> </xblock>\n	<table id=\"list\" lay-filter=\"listTable\" class=\"layui-table\" ></table>\n	\n </div>\n	<script src=\"\\${ctxPath}/static/js/view/system/${boot.uncap(g.entityName)}/${boot.uncap(g.entityName)}List.js\"></script>\n</body>\n</html>', 'Html', '2019-02-11 16:05:34', null);
INSERT INTO `t_code_template_file` VALUES ('168', '163', '<!-- 让IE8/9支持媒体查询，从而兼容栅格 -->\n<!--[if lt IE 9]>\n      <script src=\"https://cdn.staticfile.org/html5shiv/r29/html5.min.js\"></script>\n      <script src=\"https://cdn.staticfile.org/respond.js/1.4.2/respond.min.js\"></script>\n    <![endif]-->\n<!DOCTYPE html>\n<html>\n<head>\n<meta charset=\"utf-8\">\n<title>${g.name}</title>\n @include(\"/common/cssAndJs.html\"){}\n</head>\n<body class=\"layui-anim layui-anim-up\">\n<div class=\"x-body\">\n		<div class=\"layui-row\">\n			<form  id=\"searchForm\"  class=\"layui-form layui-col-md12 x-so searchForm\" onsubmit=\"return false\" >\n				<input type=\"text\" name=\"account\" placeholder=\"请输入账号\"      autocomplete=\"off\" class=\"layui-input\">\n				<input type=\"text\" name=\"phone\" placeholder=\"请输入手机号\"  autocomplete=\"off\" class=\"layui-input\">\n				<button id=\"search_btn\"  class=\"layui-btn  boot-search-btn\">\n					<i class=\"layui-icon\"></i> \n				</button>\n			</form>\n		</div>\n		<button class=\"layui-btn layui-btn-danger boot-ajax boot-batchDel \" boot-title=\"确认删除吗？\" boot-url=\"\\${ctxPath}/${boot.uncap(g.entityName)}/${boot.uncap(g.entityName)}BatchDel\"  >\n			<i class=\"layui-icon\"></i>批量删除\n		</button>\n		<button class=\"layui-btn boot-layer  \" boot-title=\"添加${g.name}\" boot-url=\"\\${ctxPath}/${boot.uncap(g.entityName)}/to${g.entityName}Add\"  >\n			<i class=\"layui-icon\"></i>添加\n		</button>\n		<span class=\"x-right\" style=\"line-height: 40px\"></span> </xblock>\n	<table id=\"list\" lay-filter=\"listTable\"></table>\n </div>\n	<script src=\"\\${ctxPath}/static/js/view/system/${boot.uncap(g.entityName)}/${boot.uncap(g.entityName)}List.js\"></script>\n</body>\n</html>', 'Html', '2019-02-11 16:07:03', null);
INSERT INTO `t_code_template_file` VALUES ('169', '164', '/**\n * 部门数据\n */	\n    layui.config({\n        base: ctxPath+\'/lib/layui/lay/modules/\'\n/*\n * /springboot-boot/src/main/webapp/static\n */    }).extend({   // 引用第三方的插件\n        treetable: \'treetable-lay/treetable\'\n    }).use([\'layer\', \'table\', \'treetable\',\'form\'], function () {\n        var $ = layui.jquery;\n        var table = layui.table;\n        var layer = parent.layer === undefined ? layui.layer : top.layer;\n        var treetable = layui.treetable;\n        var form = layui.form;\n        var treeParams = {\n                treeColIndex: 1,\n                treeSpid: 0,   // 树数据最顶级的父类 如果支持搜索条件的话，得改变这里的数据或者查询条件的数据\n                treeIdName: \'id\',\n                treePidName: \'pid\',\n                treeDefaultClose: true,\n                treeLinkage: false,\n                elem: \'#list\',\n                url:ctxPath+ \'/${boot.uncap(g.entityName)}/${boot.uncap(g.entityName)}List\' ,\n                page: false,\n                cols: [[\n                    {type: \'numbers\'},\n						<%for(field in t.fields){%> \n							{field: \'${field.name}\', title: \'${field.chinaName}\', align:\"center\"},\n						<%}%>\n		            {field: \'createTime\', title: \'创建时间\', align:\'center\', align:\'center\', templet:function(d){\n		                return _.formatDate(d.createTime);\n		            }},\n		            {title: \'操作\', width:170, fixed:\"right\",align:\"center\",templet:function(d){\n		            	return \'<a class=\"layui-btn layui-btn-xs boot-layer \" boot-title=\"修改\" boot-url=\"\'+ctxPath+\'/${boot.uncap(g.entityName)}/to${g.entityName}Update?id=\'+d.id+\'\">编辑</a>\'+\n		            	\'<a class=\"layui-btn layui-btn-xs layui-btn-danger boot-ajax \" boot-title=\"确认要删除吗\" boot-url=\"\'+ctxPath+\'/${boot.uncap(g.entityName)}/${boot.uncap(g.entityName)}Delete?id=\'+d.id+\'\"  >删除</a> \';\n		            }}\n                ]]\n            }\n       \n        // 展开全部\n        $(\'#btn-expand\').click(function () {\n            treetable.expandAll(\'#list\');\n        });\n        // 收缩全部\n        $(\'#btn-fold\').click(function () {\n            treetable.foldAll(\'#list\');\n        });\n        // 刷新数据\n        $(\'#btn-refresh\').click(function () {\n        	 treetable.render(treeParams);\n        });\n        //如果存在id  元素 就进行刷新  为了解决添加，会自动加载的问题\n        if(!_.isEmpty($(\"#list\"))){\n        	 treetable.render(treeParams);\n        }\n    });', 'JavaScript', '2019-02-11 16:09:25', null);
INSERT INTO `t_code_template_file` VALUES ('170', '165', '	layui.use([\'form\',\'layer\',\'laydate\',\'table\',\'laytpl\'],function(){\n	    var form = layui.form,\n	        layer = parent.layer === undefined ? layui.layer : top.layer,\n	        $ = layui.jquery,\n	        laydate = layui.laydate,\n	        laytpl = layui.laytpl,\n	        table = layui.table;\n		//列表数据\n		var tableIns = 	table.render({\n		        id : \"listTable\",\n				elem : \'#list\',\n				height :\"full-125\",\n				toolbar: true,\n				defaultToolbar: [\'filter\', \'print\', \'exports\'],\n				url :ctxPath+ \'/${boot.uncap(g.entityName)}/${boot.uncap(g.entityName)}List\' ,//数据接口\n				page : true, //开启分页,\n			        cols : [[\n			            {type: \"checkbox\", fixed:\"left\", width:50},\n						<%for(field in t.fields){%>\n							{field: \'${field.name}\', title: \'${field.chinaName}\', align:\"center\",hide:false},\n						<%}%>\n						{field: \'createTime\', title: \'创建时间\', align:\'center\', align:\'center\', templet:function(d){\n			                return _.formatDate(d.createTime);\n			            }},\n			            {title: \'操作\', width:170, fixed:\"right\",align:\"center\",templet:function(d){\n			            	return \'<a class=\"layui-btn layui-btn-xs boot-layer \" boot-title=\"修改用户\" boot-url=\"\'+ctxPath+\'/${boot.uncap(g.entityName)}/to${g.entityName}?id=\'+d.id+\'\">编辑</a>\'+\n			            	\'<a class=\"layui-btn layui-btn-xs layui-btn-danger boot-ajax \" boot-title=\"确认要删除吗\" boot-url=\"\'+ctxPath+\'/${boot.uncap(g.entityName)}/${boot.uncap(g.entityName)}Delete?id=\'+d.id+\'\"  >删除</a> \';\n			            }}\n			        ]]\n			});\n//==========================添加start==================\n\n\n		});', 'JavaScript', '2019-02-11 16:10:29', null);
INSERT INTO `t_code_template_file` VALUES ('171', '166', 'package ${g.codePackage}.controller;\nimport java.util.ArrayList;\nimport java.util.Date;\nimport java.util.List;\n\n\nimport org.springframework.beans.factory.annotation.Autowired;\nimport org.springframework.stereotype.Controller;\nimport org.springframework.validation.BindingResult;\nimport org.springframework.validation.annotation.Validated;\nimport org.springframework.web.bind.annotation.RequestMapping;\nimport org.springframework.web.bind.annotation.RequestParam;\nimport org.springframework.web.bind.annotation.ResponseBody;\n\nimport com.baomidou.mybatisplus.mapper.EntityWrapper;\nimport com.baomidou.mybatisplus.mapper.Wrapper;\nimport com.baomidou.mybatisplus.plugins.Page;\nimport com.xmindguoguo.boot.common.constant.GlobalConstant;\nimport com.xmindguoguo.boot.core.BaseController;\nimport ${g.codePackage}.model.${g.entityName}Model;\nimport ${g.codePackage}.service.I${g.entityName}Service;\nimport com.xmindguoguo.boot.util.CmUtil;\nimport com.xmindguoguo.boot.util.ToolUtil;\nimport com.xmindguoguo.boot.util.codec.CodecUtil;\nimport com.xmindguoguo.boot.util.json.JsonResult;\n\n\n\n\n/**\n * ${g.name}控制器\n *  //先暂时全部小写，后面写个方法注册进来，调整大小写\n * @author ${g.author}\n * @Date ${g.date}\n */\n\n@Controller\n@RequestMapping(\"/${boot.uncap(g.entityName)}\")\npublic class ${g.entityName}Controller extends BaseController {\n		\n	//所属子模块名称+当前功能名称\n	private String PREFIX = \"/system/${boot.uncap(g.entityName)}/\";   \n	\n    @Autowired\n    private I${g.entityName}Service ${boot.uncapInterface(g.entityName)}Service;\n	/**\n     *跳转列表页\n	*/\n	@RequestMapping(\"to${g.entityName}List\")\n    public String to${g.entityName}List() {\n        return PREFIX+\"${boot.uncap(g.entityName)}List\"; \n    }\n	\n    /**\n		查询列表数据 表格 数据时用，不用删除\n	*/ \n    @RequestMapping(\"${boot.uncap(g.entityName)}List\")\n    @ResponseBody\n    public Object ${boot.uncap(g.entityName)}List(${g.entityName}Model model, @RequestParam(value = \"page\", required = false) Integer current,\n            @RequestParam(value = \"limit\", required = false) Integer size) {\n        Wrapper<${g.entityName}Model> wrapper = new EntityWrapper<>();\n       /* if (ToolUtil.isNotEmpty(model.getAccount())) {\n            wrapper.like(\"account\", model.getAccount());\n        }\n		*/ \n        wrapper.ne(\"status\", GlobalConstant.MODEL_DEL);\n        wrapper.orderBy(\"createTime\", false);\n        Page<${g.entityName}Model> page = new Page<>(current, size);\n        return JsonResult.sendPage(${boot.uncapInterface(g.entityName)}Service.selectPage(page, wrapper)); // 跳转到列表页\n    }\n	/**\n		查询列表数据  tree 数据时用，不用删除\n	*/ \n    @RequestMapping(\"${boot.uncap(g.entityName)}List\")\n    @ResponseBody\n    public Object ${boot.uncap(g.entityName)}List(${g.entityName}Model model) {\n        Wrapper<${g.entityName}Model> wrapper = new EntityWrapper<>();\n       /* if (ToolUtil.isNotEmpty(model.getAccount())) {\n            wrapper.like(\"account\", model.getAccount());\n        }\n		*/ \n        wrapper.ne(\"status\", GlobalConstant.MODEL_DEL);\n        wrapper.orderBy(\"createTime\", false);\n        return JsonResult.sendOk(${boot.uncapInterface(g.entityName)}Service.selectList( wrapper)); // 跳转到列表页\n    }\n\n    @RequestMapping(\"to${g.entityName}Add\")\n    public String to${g.entityName}Add() {\n        return PREFIX+\"${boot.uncap(g.entityName)}Add\"; \n    }\n	/**\n	*   添加model\n	*/\n    @RequestMapping(\"${boot.uncap(g.entityName)}Add\")\n    @ResponseBody\n    public Object ${boot.uncap(g.entityName)}Add(@Validated ${g.entityName}Model model, BindingResult result) {\n        if (result.hasErrors()) {\n            return sendValidatedMsg(result);\n        }\n        model.setId(CmUtil.getPkId());\n        model.setStatus(GlobalConstant.MODEL_ADD);\n        model.setCreateTime(new Date());\n		setPids(model);\n        boolean flag = ${boot.uncapInterface(g.entityName)}Service.insert(model);\n        return flag ? JsonResult.sendOk(\"添加成功\", null) : JsonResult.sendError(\"添加失败\", null);\n    }\n    private void setPids(${g.entityName}Model  model) {\n        if (model.getPId() == null) {\n            model.setPId(0L);\n            model.setPIds(model.getId() + \"\");\n        }\n        if (model.getPId() != 0L) {\n            ${g.entityName}Model oldModel = ${boot.uncapInterface(g.entityName)}Service.selectById(model.getPId());\n            model.setPIds(oldModel.getPIds() + \"_\" + model.getId());\n        }\n    }\n    @RequestMapping(\"to${g.entityName}Update\")\n    public String to${g.entityName}Update(Long id) {\n        ${g.entityName}Model model = ${boot.uncapInterface(g.entityName)}Service.selectById(id);\n        request.setAttribute(\"model\", model);\n        return PREFIX+\"${boot.uncap(g.entityName)}Update\"; \n    }\n	/**\n	*   更新model\n	*/\n    @RequestMapping(\"${boot.uncap(g.entityName)}Update\")\n    @ResponseBody\n    public Object ${boot.uncap(g.entityName)}Update(@Validated ${g.entityName}Model model, BindingResult result) {\n        if (result.hasErrors()) {\n            return sendValidatedMsg(result);\n        }\n		\n        model.setUpdateTime(new Date());\n				setPids(model);\n\n        boolean flag = ${boot.uncapInterface(g.entityName)}Service.updateById(model);\n        return flag ? JsonResult.sendOk(\"更新成功\", null) : JsonResult.sendError(\"更新失败\", null);\n    }\n\n    @RequestMapping(\"${boot.uncap(g.entityName)}Delete\")\n    @ResponseBody\n	public Object	${boot.uncap(g.entityName)}Delete(Long id) {\n        ${g.entityName}Model model = new ${g.entityName}Model();\n        model.setStatus(GlobalConstant.MODEL_DEL);\n        model.setId(id);\n        boolean flag = ${boot.uncapInterface(g.entityName)}Service.updateById(model);\n        return flag ? JsonResult.sendOk(\"删除成功\", null) : JsonResult.sendError(\"删除失败\", null);\n    }\n\n    @RequestMapping(\"${boot.uncap(g.entityName)}BatchDel\")\n    @ResponseBody\n    public Object ${boot.uncap(g.entityName)}BatchDel(String ids) {\n        Long[] idsArray = ConvertUtil.StringToLongArray(ids);\n        List<${g.entityName}Model> list = new ArrayList<>();\n        ${g.entityName}Model model = null;\n        for (Long id : idsArray) {\n            model = new ${g.entityName}Model();\n            model.setStatus(GlobalConstant.MODEL_DEL);\n            model.setId(id);\n            list.add(model);\n        }\n        boolean flag = ${boot.uncapInterface(g.entityName)}Service.updateBatchById(list);\n        return flag ? JsonResult.sendOk(\"删除成功\", null) : JsonResult.sendError(\"删除失败\", null);\n    }\n\n    \n}', 'Java', '2019-02-16 11:09:06', null);
